      out.write("    // 전체 버튼 클래스 상태 확인\n");
      out.write("    if ($this.hasClass(\"checked\")) {\n");
      out.write("        $this.removeClass(\"checked\"); // 클래스 제거\n");
      out.write("        $this.css(\"background-color\", \"#eee\"); // 색상 변경\n");
      out.write("        $(\".otherCheck\").removeClass(\"checked\").css(\"background-color\", \"#eee\"); // 상품 버튼들 색상 변경\n");
      out.write("    } else {\n");
      out.write("        $this.addClass(\"checked\"); // 클래스 추가\n");
      out.write("        $this.css(\"background-color\", \"black\"); // 색상 변경\n");
      out.write("        $(\".otherCheck\").addClass(\"checked\").css(\"background-color\", \"black\"); // 상품 버튼들 색상 변경\n");
      out.write("    }\n");
      out.write("    calculateTotal();\n");
