      out.write("</script>\n");
      out.write("\n");
      out.write("<script>\n");
      out.write("$(document).ready(function() {\n");
      out.write("    calculateTotal(); // 페이지 로드 시 총합 계산\n");
      out.write("});\n");
      out.write("\n");
      out.write("//총 합계 계산 함수\n");
      out.write("function calculateTotal() {\n");
      out.write("    let totalPrice = 0;\n");
      out.write("    let totalQuantity = 0;\n");
      out.write("    let totalDiscount = 0;\n");
      out.write("\n");
      out.write("    // 체크된 상품들만 선택하여 가격과 수량 더하기\n");
      out.write("    $(\".otherCheck.checked\").each(function() {\n");
      out.write("        const price = parseFloat($(this).data(\"price\")); // data-price에서 가격 추출\n");
      out.write("        const quantity = parseInt($(this).data(\"quantity\")); // data-quantity에서 수량 추출\n");
      out.write("        const discountRate = parseFloat($(this).data(\"rate\")) / 100; // data-rate에서 할인율 추출\n");
      out.write("\n");
      out.write("        if (!isNaN(price) && !isNaN(quantity)) {\n");
      out.write("            totalPrice += price * quantity; // 가격 * 수량 더하기\n");
      out.write("            totalQuantity += quantity; // 수량 더하기\n");
      out.write("            \n");
      out.write("         	// 할인 금액 계산 (상품 가격 * 할인율 * 수량)\n");
      out.write("            const discountAmount = price * discountRate * quantity;\n");
      out.write("            totalDiscount += discountAmount; // 할인 금액 더하기\n");
      out.write("        }\n");
      out.write("    });\n");
      out.write("\n");
      out.write("    // 총 가격을 화면에 출력\n");
      out.write("    console.log(\"총 가격: \" + totalPrice + \"원\");\n");
      out.write("    console.log(\"총 수량: \" + totalQuantity + \"개\");\n");
      out.write("\n");
      out.write("    // 선택된 상품들의 총합을 prdPriceAll에 출력\n");
      out.write("    $(\".prdPriceAll\").text(totalPrice.toLocaleString());\n");
      out.write("    \n");
      out.write(" 	// 배송비 계산\n");
      out.write("    let deliveryFee = (totalPrice >= 50000) ? 0 : 3000; // 총합이 50,000원 이상이면 0, 아니면 3000원\n");
      out.write("    $(\".prdDelvAll\").text(deliveryFee.toLocaleString()); // 배송비 출력\n");
      out.write("    \n");
      out.write(" 	// 추가 구매 금액 계산\n");
      out.write("    const additionalAmount = 50000 - totalPrice; // 50,000원에서 현재 총합을 뺀 금액\n");
      out.write("    if (additionalAmount > 0) {\n");
      out.write("        // 총합이 50,000원 미만이면 추가 금액 표시\n");
      out.write("        $(\".amount\").text(additionalAmount.toLocaleString()); // 추가 금액 출력\n");
      out.write("    } else {\n");
      out.write("        $(\".amount\").text(\"\"); // 50,000원 이상이면 빈 문자열 출력 (안 보이게)\n");
      out.write("    }\n");
      out.write("    \n");
      out.write("    $(\".prdDiscountAll\").text(totalDiscount.toLocaleString());\n");
      out.write("    \n");
      out.write(" 	// 할인율 적용 후 총합 계산 (배송비 포함)\n");
      out.write("    const finalTotalPrice = totalPrice - totalDiscount + deliveryFee; // 할인 금액을 빼고 배송비를 더해서 최종 가격 계산\n");
      out.write("    $(\".prdFinalAll\").text(finalTotalPrice.toLocaleString()); // 최종 가격 출력\n");
      out.write("}\n");
      out.write("</script>\n");
      out.write("\n");
