      out.write("    getBasketCookie() {\r\n");
      out.write("        const cookie = document.cookie\r\n");
      out.write("            .split('; ')\r\n");
      out.write("            .find(row => row.startsWith('basketItems='));\r\n");
      out.write("        return cookie ? cookie.split('=')[1] : null;\r\n");
      out.write("    },\r\n");
