      out.write("        if (currentCookie !== this.lastCookieValue) {\r\n");
      out.write("            this.lastCookieValue = currentCookie;\r\n");
      out.write("            this.updateCount();\r\n");
      out.write("        }\r\n");
      out.write("    },\r\n");
