      out.write("<script>\r\n");
      out.write("document.addEventListener('DOMContentLoaded', function () {\r\n");
      out.write("    const selectAllCheck = document.getElementById('product_select_all');\r\n");
      out.write("    const individualChecks = document.querySelectorAll('.individual-checkbox');\r\n");
      out.write("    const totalItemCount = document.querySelector('.all-count');\r\n");
      out.write("\r\n");
      out.write("    // 가격 관련 요소들\r\n");
      out.write("    const totalProductPrice = document.querySelector('.prdPriceAll');\r\n");
      out.write("    const totalDiscountPrice = document.querySelector('.prdDiscountAll');\r\n");
      out.write("    const shippingFee = document.querySelector('.prdDelvAll');\r\n");
      out.write("    const finalPrice = document.querySelector('.prdFinalAll');\r\n");
      out.write("\r\n");
      out.write("    // 전체 상품 개수 업데이트\r\n");
      out.write("    if (totalItemCount) {\r\n");
      out.write("        totalItemCount.textContent = individualChecks.length;\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
      out.write("    // 숫자 포맷팅 (천단위 콤마)\r\n");
      out.write("    function addCommas(num) {\r\n");
      out.write("        return Math.round(num).toString().replace(/\\B(?=(\\d{3})+(?!\\d))/g, \",\");\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
      out.write("    // 금액 표시 업데이트 헬퍼 함수\r\n");
      out.write("    function updatePrice(selector, amount) {\r\n");
      out.write("        const element = document.querySelector(selector);\r\n");
      out.write("        if (element) {\r\n");
      out.write("            element.textContent = addCommas(amount);\r\n");
      out.write("        } else {\r\n");
      out.write("            console.warn(\"Element not found for selector:\", selector);\r\n");
      out.write("        }\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
      out.write("    // 전체 선택/해제 체크박스 상태 업데이트\r\n");
      out.write("    function updateSelectAllStatus() {\r\n");
      out.write("        const allChecked = Array.from(individualChecks).every(cb => cb.checked);\r\n");
      out.write("        if (selectAllCheck) {\r\n");
      out.write("            selectAllCheck.checked = allChecked;\r\n");
      out.write("            updateAllCheckStyle();\r\n");
      out.write("        }\r\n");
      out.write("        calculateTotalPrice();\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
      out.write("    // 전체 선택 체크박스 스타일 업데이트\r\n");
      out.write("    function updateAllCheckStyle() {\r\n");
      out.write("        const allCheckDiv = document.querySelector('.allCheck');\r\n");
      out.write("        if (allCheckDiv) {\r\n");
      out.write("            if (selectAllCheck.checked) {\r\n");
      out.write("                allCheckDiv.classList.add('checked');\r\n");
      out.write("            } else {\r\n");
      out.write("                allCheckDiv.classList.remove('checked');\r\n");
      out.write("            }\r\n");
      out.write("        }\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
      out.write("    // 개별 체크박스 스타일 업데이트\r\n");
      out.write("    function updateCheckboxStyle(checkbox) {\r\n");
      out.write("        const checkDiv = checkbox.nextElementSibling.querySelector('.otherCheck');\r\n");
      out.write("        if (checkDiv) {\r\n");
      out.write("            if (checkbox.checked) {\r\n");
      out.write("                checkDiv.classList.add('checked');\r\n");
      out.write("            } else {\r\n");
      out.write("                checkDiv.classList.remove('checked');\r\n");
      out.write("            }\r\n");
      out.write("        }\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
      out.write("    // 총 금액 계산 함수\r\n");
      out.write("    function calculateTotalPrice() {\r\n");
      out.write("        let originalTotal = 0; // 총 상품금액\r\n");
      out.write("        let discountTotal = 0; // 총 할인금액\r\n");
      out.write("        let totalAfterDiscount = 0; // 할인 적용 후 금액\r\n");
      out.write("\r\n");
      out.write("        // 체크된 상품들에 대해 계산\r\n");
      out.write("        document.querySelectorAll('.individual-checkbox:checked').forEach(function (checkbox) {\r\n");
      out.write("            const priceInfo = checkbox.nextElementSibling.querySelector('.otherCheck');\r\n");
      out.write("            if (!priceInfo) return;\r\n");
      out.write("\r\n");
      out.write("            const price = +(priceInfo.getAttribute('data-price'));\r\n");
      out.write("            const qty = +(priceInfo.getAttribute('data-quantity'));\r\n");
      out.write("            const rate = +(priceInfo.getAttribute('data-rate'));\r\n");
      out.write("\r\n");
      out.write("            if (!isNaN(price) && !isNaN(qty) && !isNaN(rate)) {\r\n");
      out.write("                if (rate >= 0) {\r\n");
      out.write("                    originalTotal += price * qty;\r\n");
      out.write("                    discountTotal += ((price / 100) * (100 - rate)) * qty;\r\n");
      out.write("                } else {\r\n");
      out.write("                    originalTotal += price * qty;\r\n");
      out.write("                    discountTotal += price * qty;\r\n");
      out.write("                }\r\n");
      out.write("\r\n");
      out.write("                // 개별 상품의 합계 업데이트\r\n");
      out.write("                const itemTotalEl = priceInfo.closest('.prdInfo').querySelector('.prdTotal strong');\r\n");
      out.write("                if (itemTotalEl) {\r\n");
      out.write("                    itemTotalEl.textContent = addCommas(price * qty);\r\n");
      out.write("                }\r\n");
      out.write("            }\r\n");
      out.write("        });\r\n");
      out.write("\r\n");
      out.write("        totalAfterDiscount = originalTotal - discountTotal;\r\n");
      out.write("\r\n");
      out.write("        // 최종 금액 계산\r\n");
      out.write("        const shipping = totalAfterDiscount >= 50000 ? 0 : 3000;\r\n");
      out.write("        const finalTotal = discountTotal + shipping;\r\n");
      out.write("\r\n");
      out.write("        // UI 업데이트\r\n");
      out.write("        updatePrice('.prdPriceAll', originalTotal);       // 총 상품금액\r\n");
      out.write("        updatePrice('.prdDiscountAll', totalAfterDiscount); // 총 할인금액\r\n");
      out.write("        updatePrice('.prdDelvAll', shipping);             // 배송비\r\n");
      out.write("        updatePrice('.prdFinalAll', finalTotal);          // 최종 결제금액\r\n");
      out.write("\r\n");
      out.write("        console.log(\"totalAfterDiscount값은 \" + totalAfterDiscount);\r\n");
      out.write("        // 무료배송까지 남은 금액 표시\r\n");
      out.write("        const delvInfo = document.querySelector('.total_info_txt.delv');\r\n");
      out.write("        if (delvInfo) {\r\n");
      out.write("            if (discountTotal >= 50000) {\r\n");
